import React,{useContext} from 'react'
import { FiSearch } from 'react-icons/fi';


import {
    LogoImage,
    HeaderSectionContainer,
    HeaderSection,
    HeaderTitle,
    HeaderInputContainer,
    HeaderInput,
    HeaderSearchButton,


} from './styles';
import { Link } from 'react-router-dom';
import api from '../../services/api'
import { context } from '../../context';

const Input = () => {
    const ctx = useContext(context)



    async function getApiData(){
        try {
            const response = await api.get(`${ctx.search}`)
            const repos = await api.get(`${ctx.search}/repos`)
            const followers = await api.get(`${ctx.search}/followers`)
            const following = await api.get(`${ctx.search}/following`)
            ctx.SetUserData(response.data)
            ctx.SetRepos(repos.data)
            ctx.SetFollowers(followers.data)
            ctx.SetFollowing(following.data)
        } catch (err) {
            ctx.setHasError(true)
        }

    }


    return (

    <HeaderSectionContainer>
    <LogoImage placeholder={"Digite o usuário..."} src={'https://image.flaticon.com/icons/png/512/25/25657.png'} alt={"Logo do GitHub"} />
    <HeaderSection>
        <HeaderTitle>Digite o nome do usuário:</HeaderTitle>
        <HeaderInputContainer>

            <HeaderInput value={ctx.search} onChange={e => ctx.SetSearch(e.target.value)} />

            <Link to="/Home" >

            <HeaderSearchButton onClick={()=>getApiData()}>
                <FiSearch size={15} />
            </HeaderSearchButton>

            </Link>

        </HeaderInputContainer>
    </HeaderSection>
    </HeaderSectionContainer>
)};

export default Input;
