import React,{useContext,useState} from 'react'
import HomeContainer from '../components/HomeContainer/index'
import Repos_Page from '../components/Repos_page'
import Card from '../components/Repos_Card';


import { context } from '../context';





const Repos = () => {

    const ctx = useContext(context)
    const [data,SetData] = useState([ctx.repos])
    return (
        <div>
            <HomeContainer>
            <Repos_Page>

            {data.map(repo =>
                    <Card
                    number = {ctx.userData.public_repos}
                    title = {repo.name}
                    description = {repo.description}
                    star = {repo.stargazers_count}
                    />

            )}
            </Repos_Page>





            </HomeContainer>

        </div>
    )
}

export default Repos
