import styled from "styled-components";

export const Section = styled.section`

    width: 400px;
    height: 100vh;
    background-color: rgba(41,41,41,255);
    border-radius: 10px;
    border-bottom: 10px solid #FF7A00;
    margin-top: 30px;
    margin-bottom: 30px;
    overflow-y: scroll;
    @media (max-width:900px){
        width: 300px;
        height: 80vh;

    }


`

export const Header = styled.section`
background-color: black;
height: 5vh;
display: flex;

`
export const RepoNumber = styled.h2`
width: 100%;
height: 100%;
margin-left: 30%;
color: white;
display: flex;
font-size: 0.4cm;
display: flex;
flex-direction: row;
align-items: center;
justify-content: flex-start;



`

export const Button = styled.div`
cursor: pointer;
margin-left: 7px;
height: 100%;
width: 100%;
color: white;
display: flex;
justify-content: flex-start;
align-items: center;
text-decoration: none;



`


export const ImagemContainer = styled.div`
width: 100%;
display: flex;
flex-direction: row;
justify-content: space-between;
align-items: center;
background-color: rgba(31,31,31,255);
height: 15vh;
color: white;
cursor: pointer;

`


export const Div = styled.div`
margin-top: 20px;
height: 0.1vh;
background-color: rgba(64,64,64,255);`


export const Imagem = styled.img`
margin-left: 15px;
width: 70px;
height: 70px;
border-radius:50%;
border:4px solid white
`
