import styled from 'styled-components';

export const Section = styled.section`
    width: 350px;
    height: 100vh;
    background-color: rgba(41,41,41,255);
    border-radius: 10px;
    border-bottom: 10px solid #FF7A00;
    margin-top: 30px;
    margin-bottom: 30px;
    flex-direction:row;
    @media (max-width:900px){
        width: 300px;
    }


`
export const LogOut = styled.div`
color: white;
display: flex;
cursor: pointer;
align-items: center;
margin-top: 10px;
`

export const ContainerLogOut = styled.div `
width: 100%;
height: 25vh;
border-radius: 10px;

background-color: rgba(31,31,31,255);

`
export const Div = styled.div `
display: flex;
justify-content: space-between;
margin-right: 10px;
`
export const AnotherDiv = styled.div`
margin-right: 10px;
font-size: 17px;
cursor: pointer;

`
export const Nome = styled.div `
color: white;
margin-top: 10px;
margin-left: 15px;
`
export const Figure = styled.div `
display: flex;
justify-content: center;
margin-bottom: 20%;
`
export const Imagem = styled.div`
width: 130px;
height: 130px;
border-radius:50%;
border:8px solid white;
`
