import React,{useState,useContext} from 'react'
import { FiSearch } from 'react-icons/fi';


import {
    LogoImage,
    HeaderSectionContainer,
    HeaderSection,
    HeaderTitle,
    HeaderInputContainer,
    HeaderInput,
    HeaderSearchButton,
    AnotherRoute

} from './styles';

import api from '../../services/api'
import { context } from '../../context';

const Input = () => {
    const ctx = useContext(context)

    const [search, setSearch] = useState(``);

    async function getApiData(){
        try {
            const response = await api.get(`${search}`)
            ctx.setUserData(response.data)
        } catch (err) {
            console.log(err)
        }

    }


    return (

    <HeaderSectionContainer>
        {console.log(ctx)}
    <LogoImage placeholder={"Digite o usuário..."} src={'https://image.flaticon.com/icons/png/512/25/25657.png'} alt={"Logo do GitHub"} />
    <HeaderSection>
        <HeaderTitle>Digite o nome do usuário:</HeaderTitle>
        <HeaderInputContainer>
            <HeaderInput value={search} onChange={e => setSearch(e.target.value)} />
            <AnotherRoute >
            <HeaderSearchButton onClick={()=>getApiData()}>
                <FiSearch size={15} />
            </HeaderSearchButton>
            </AnotherRoute>
        </HeaderInputContainer>
    </HeaderSection>
    </HeaderSectionContainer>
)};

export default Input;
