import styled from 'styled-components';

export const HeaderSectionContainer = styled.section`
    width: 400px;
    height: 200px;
    background-size: cover;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;

`
export const LogoImage = styled.section`
background-image: url("https://www.google.com/url?sa=i&url=https%3A%2F%2Fwww.iconsdb.com%2Fyellow-icons%2Fgithub-6-icon.html&psig=AOvVaw3hIE775I-84x79dP36LrD-&ust=1633917152754000&source=images&cd=vfe&ved=0CAkQjRxqFwoTCNiP8PHdvvMCFQAAAAAdAAAAABAa");

`
export const HeaderSection = styled.header`
    width: 500px;
    height: 250px;
    background: rgba(255,255,255, .2);
    border-radius: 10px;
    display: flex;
    flex-direction: column;
    padding: 0;
`

export const HeaderTitle = styled.h1`
    margin: 0;
    padding: 15px 0;
    color: #E5E5E5;
    font-size: 30px;
    font-weight: 700;
    text-align: center;
    width: 100%;
`

export const HeaderInputContainer = styled.div`
    padding: 10px 16px;
    display: flex;
    align-items: center;
    justify-content: center;
    flex-direction: column;
`

export const HeaderInput = styled.input`
    width: 250px;
    height: 40px;
    margin-top: 1rem;
    border: none;
    padding: 0 16px;
    color: #444;
    transition: .4s ease;

    &:focus {
        outline: none;
        width: 300px;
        transition: .4s ease;
    }
`

export const HeaderSearchButton = styled.button`
    padding: 0 16px;

    width: 300px;
    height: 40px;
    margin:auto;
    margin-top: 20px;
    border: none;
    background: #FF7A00;
    display: flex;
    align-items: center;
    justify-content: center;
    color: #FFF;
    transition: ease .3s;


    &:hover {
        transform: scale(1.05);
        transition: ease .3s;
        cursor: pointer;
    }
`
