import styled from 'styled-components';

export const Section = styled.section`
    width: 300px;
    height: 100vh;
    background-color: rgba(31,31,31,255);
    border-radius: 10px;
    border-bottom: 10px solid #FF7A00;
    margin-top: 30px;
    margin-bottom: 30px;
    flex-direction:row;


`
export const LogOut = styled.div`
color: white;
display: flex;
`

export const ContainerLogOut = styled.div `
background: white;
width: 100%;
height: 30vh;
background-color: rgba(31,31,31,255);
`
export const Div = styled.div `
display: flex;
justify-content: space-between;
margin-top: 10px;
margin-right: 10px;
`
export const AnotherDiv = styled.div`
margin-right: 30px;

`
